import { useState } from "react";
import { Alert, Button, Col, Container, Form, Row } from "react-bootstrap";
import { login } from "../services/AdminService";
import { useNavigate } from "react-router-dom";

export function Login() {
    const navigate=useNavigate();
    const [formData,setFormData]=useState({phone:"",password:""});
    const [loginError,setLoginError]=useState(false);

    const handleChange=(e)=>{
        setFormData({...formData,[e.target.name]:e.target.value});
    }
    const handleSubmit=async(e)=>{
        e.preventDefault();
        try {
          const result= await login(formData);
          localStorage.setItem("token",result.token);
          navigate("/dashboard");
        } catch (error) {
            console.log(error);
            setLoginError(true);
        }
    }
    const shoot = () => {
        alert("Login Successful click on ok!");
      }

    return (
        <Container className="mt-5">
            <Row>
                <Col lg={6}>
                    <Form onSubmit={handleSubmit}>
                        <Form.Group className="mb-3">
                            <Form.Label>Phone</Form.Label>
                            <Form.Control type="text" placeholder="Enter Phone" name="phone" onChange={handleChange} />
                        </Form.Group>

                        <Form.Group className="mb-3" controlId="formBasicPassword">
                            <Form.Label>Password</Form.Label>
                            <Form.Control type="password" placeholder="Password" name="password" onChange={handleChange} />
                        </Form.Group>
                        
                        <Button variant="primary" type="submit"  onClick={shoot} >
                            Login
                        </Button>
                    </Form>
                    {loginError?<Alert variant="danger" className="mt-3">Invalid phone or password</Alert>:null}
                </Col>
            </Row>
        </Container>
    );
}
